// HMAC-SHA256 Verification
// Verify X-Signature header against body using N8N_WEBHOOK_SECRET

const crypto = require('crypto');

const signature = $input.first().json.headers['x-signature'];
const body = JSON.stringify($input.first().json.body);
const secret = $env.N8N_WEBHOOK_SECRET;

if (!secret) {
  throw new Error('N8N_WEBHOOK_SECRET environment variable is not set');
}

if (!signature) {
  throw new Error('Missing X-Signature header');
}

const expectedSignature = crypto
  .createHmac('sha256', secret)
  .update(body)
  .digest('hex');

const isValid = crypto.timingSafeEqual(
  Buffer.from(signature, 'hex'),
  Buffer.from(expectedSignature, 'hex')
);

if (!isValid) {
  throw new Error('Invalid HMAC signature');
}

// Pass through the verified body
return [{ json: $input.first().json.body }];

// Initialize PipelineState
// Extract fields from webhook body and build initial pipeline state

const input = $input.first().json;

const pipelineState = {
  version: 'v1.1',
  campaignId: input.campaignId,
  mode: input.mode || 'pro',
  status: 'running',
  agentErrors: [],
  startedAt: new Date().toISOString()
};

return [{
  json: {
    campaignId: input.campaignId,
    brief: input.brief,
    brandProfile: input.brandProfile,
    agentConfig: input.agentConfig,
    mode: input.mode || 'pro',
    brandMemory: input.brandMemory || null,
    pipelineVersion: input.pipelineVersion || 'v1.1',
    pipelineState
  }
}];

// Progress Callback Helper
// Sends progress updates back to Next.js via HMAC-signed POST
//
// Usage: Set $json.callbackPayload before this node with the payload to send.
// The node will sign it and POST to NEXTJS_CALLBACK_URL.

const crypto = require('crypto');

const callbackUrl = $env.NEXTJS_CALLBACK_URL;
const secret = $env.N8N_WEBHOOK_SECRET;

if (!callbackUrl) {
  throw new Error('NEXTJS_CALLBACK_URL environment variable is not set');
}

const payload = $input.first().json.callbackPayload;
if (!payload) {
  // No callback payload set, pass through
  return [$input.first()];
}

const body = JSON.stringify(payload);
const signature = crypto
  .createHmac('sha256', secret)
  .update(body)
  .digest('hex');

const response = await fetch(callbackUrl, {
  method: 'POST',
  headers: {
    'Content-Type': 'application/json',
    'X-Signature': signature
  },
  body
});

if (!response.ok) {
  console.warn(`Callback failed: HTTP ${response.status}`);
}

// Pass through pipeline data (excluding callbackPayload)
const { callbackPayload: _, ...rest } = $input.first().json;
return [{ json: rest }];

// Merge Copywriter and Art Director parallel branches
// Combines outputs from both branches into a single PipelineState.
// Branch 1 (Copywriter) contributes pipelineState.copywriter
// Branch 2 (Art Director) contributes pipelineState.artDirector
// Both may also contribute agentErrors for partial-delivery.

const items = $input.all();

if (items.length < 2) {
  // Only one branch completed (edge case: one branch errored completely)
  return [items[0]];
}

// Deep merge: take base from first item, overlay second item's unique fields
const branch1 = items[0].json;
const branch2 = items[1].json;

// Determine which branch has copywriter and which has artDirector
const hasCopywriter1 = !!branch1.pipelineState?.copywriter;
const hasArtDirector1 = !!branch1.pipelineState?.artDirector;
const hasCopywriter2 = !!branch2.pipelineState?.copywriter;
const hasArtDirector2 = !!branch2.pipelineState?.artDirector;

// Merge pipelineState: combine copywriter and artDirector from both branches
const mergedPipelineState = {
  ...branch1.pipelineState,
  // Take copywriter from whichever branch has it
  copywriter: branch1.pipelineState?.copywriter || branch2.pipelineState?.copywriter || undefined,
  // Take artDirector from whichever branch has it
  artDirector: branch1.pipelineState?.artDirector || branch2.pipelineState?.artDirector || undefined,
  // Merge agentErrors from both branches
  agentErrors: [
    ...(branch1.pipelineState?.agentErrors || []),
    ...(branch2.pipelineState?.agentErrors || []).filter(
      e2 => !(branch1.pipelineState?.agentErrors || []).some(e1 => e1.agentName === e2.agentName && e1.timestamp === e2.timestamp)
    )
  ]
};

const merged = {
  ...branch1,
  pipelineState: mergedPipelineState
};

return [{ json: merged }];

