// Final Callback: Send success status with full pipelineState

const crypto = require('crypto');

const input = $input.first().json;
const callbackUrl = $env.NEXTJS_CALLBACK_URL;
const secret = $env.N8N_WEBHOOK_SECRET;

if (!callbackUrl) {
  throw new Error('NEXTJS_CALLBACK_URL environment variable is not set');
}

// Mark pipeline as complete
const pipelineState = {
  ...input.pipelineState,
  status: 'complete',
  completedAt: new Date().toISOString()
};

const payload = {
  campaignId: input.campaignId,
  status: 'success',
  pipelineVersion: 'v1.1',
  pipelineState,
  agentStep: {
    agentName: 'video_pipeline',
    labelJa: '\u52d5\u753b\u751f\u6210\u4e2d',
    status: 'complete',
    completedAt: new Date().toISOString()
  }
};

const body = JSON.stringify(payload);
const signature = crypto
  .createHmac('sha256', secret)
  .update(body)
  .digest('hex');

const response = await fetch(callbackUrl, {
  method: 'POST',
  headers: {
    'Content-Type': 'application/json',
    'X-Signature': signature
  },
  body
});

return [{ json: { success: true, pipelineState } }];
